// src/JsonInputForm.js
import React, { useState } from 'react';
import axios from 'axios';

const JsonInputForm = () => {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState('');
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
    setError('');
  };

  const handleSelectChange = (e) => {
    const options = Array.from(e.target.selectedOptions, option => option.value);
    setSelectedOptions(options);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const jsonData = JSON.parse(input);
      const res = await axios.post('http://localhost:YOUR_BACKEND_PORT/bfhl', jsonData);
      setResponse(res.data);
    } catch (err) {
      setError('Invalid JSON format or API call failed.');
    }
  };

  const renderResponse = () => {
    if (!response) return null;

    const { numbers, alphabets, highest_lowercase_alphabet } = response;
    const selectedData = {};

    if (selectedOptions.includes('Numbers')) {
      selectedData.numbers = numbers;
    }
    if (selectedOptions.includes('Alphabets')) {
      selectedData.alphabets = alphabets;
    }
    if (selectedOptions.includes('Highest lowercase alphabet')) {
      selectedData.highest_lowercase_alphabet = highest_lowercase_alphabet;
    }

    return (
      <div>
        <h3>Response:</h3>
        <pre>{JSON.stringify(selectedData, null, 2)}</pre>
      </div>
    );
  };

  return (
    <div>
      <h1>Your Roll Number</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={input}
          onChange={handleInputChange}
          placeholder='Enter JSON here...'
          rows='4'
          style={{ width: '100%' }}
        />
        <button type='submit'>Submit</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {response && (
        <div>
          <label>
            <select multiple={true} onChange={handleSelectChange}>
              <option value='Alphabets'>Alphabets</option>
              <option value='Numbers'>Numbers</option>
              <option value='Highest lowercase alphabet'>Highest lowercase alphabet</option>
            </select>
          </label>
          {renderResponse()}
        </div>
      )}
    </div>
  );
};

export default JsonInputForm;
